import React, { Component } from 'react';
import runes from './lol_data/runesReforged.json';
import spells from './lol_data/summoner.json';
import items from './lol_data/item.json';
import champions from './lol_data/champion.json';
import './App.css';

class App extends Component {
   constructor() {
      super();

      this.state = {
         summonerName: "",
         summonerMatchData: [{"gameId":2972291798,"win":true,"summonerName":"C9 Sneaky","gameDuration":1797,"championId":203,"kills":4,"deaths":8,"assists":12,"kda":"2.00","spell1Id":4,"spell2Id":7,"item0":3153,"item1":2421,"item2":3094,"item3":1054,"item4":3006,"item5":1038,"item6":3363,"champLevel":14,"creepscore":205,"creepscorepermin":6.844741235392321,"primaryPerkStyle":8000,"primaryPerk":8005,"subPerkStyle":8200},{"gameId":2972268622,"win":true,"summonerName":"C9 Sneaky","gameDuration":1328,"championId":236,"kills":9,"deaths":4,"assists":10,"kda":"4.75","spell1Id":4,"spell2Id":7,"item0":3153,"item1":3094,"item2":3006,"item3":1055,"item4":1038,"item5":1037,"item6":3363,"champLevel":12,"creepscore":176,"creepscorepermin":7.951807228915663,"primaryPerkStyle":8000,"primaryPerk":8005,"subPerkStyle":8200},{"gameId":2972264757,"win":false,"summonerName":"C9 Sneaky","gameDuration":1502,"championId":29,"kills":5,"deaths":5,"assists":6,"kda":"2.20","spell1Id":4,"spell2Id":7,"item0":1054,"item1":3031,"item2":3006,"item3":3085,"item4":3086,"item5":0,"item6":3363,"champLevel":13,"creepscore":185,"creepscorepermin":7.390146471371504,"primaryPerkStyle":8000,"primaryPerk":8008,"subPerkStyle":8200},{"gameId":2972218014,"win":true,"summonerName":"C9 Sneaky","gameDuration":2226,"championId":145,"kills":11,"deaths":5,"assists":2,"kda":"2.60","spell1Id":4,"spell2Id":7,"item0":3031,"item1":3140,"item2":3085,"item3":3006,"item4":3124,"item5":3036,"item6":3363,"champLevel":17,"creepscore":318,"creepscorepermin":8.571428571428571,"primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8200},{"gameId":2972221824,"win":false,"summonerName":"C9 Sneaky","gameDuration":1546,"championId":51,"kills":1,"deaths":7,"assists":3,"kda":"0.57","spell1Id":4,"spell2Id":7,"item0":3031,"item1":3087,"item2":3006,"item3":2031,"item4":1055,"item5":0,"item6":3363,"champLevel":12,"creepscore":176,"creepscorepermin":6.830530401034929,"primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8200},{"gameId"