import React, { Component } from 'react';
import runes from './lol_data/runesReforged.json';
import spells from './lol_data/summoner.json';
import items from './lol_data/item.json';
import champions from './lol_data/champion.json';
import './App.css';

class App extends Component {
   constructor() {
      super();

      this.state = {
         summonerName: "",
         summonerMatchData: [{"gameId":2972291798,"win":true,"summonerName":"C9 Sneaky","gameDuration":1797,"championId":203,"kills":4,"deaths":8,"assists":12,"kda":"2.00","spell1Id":4,"spell2Id":7,"item0":3153,"item1":2421,"item2":3094,"item3":1054,"item4":3006,"item5":1038,"item6":3363,"champLevel":14,"creepscore":205,"creepscorepermin":"6.8","primaryPerkStyle":8000,"primaryPerk":8005,"subPerkStyle":8200},{"gameId":2972268622,"win":true,"summonerName":"C9 Sneaky","gameDuration":1328,"championId":236,"kills":9,"deaths":4,"assists":10,"kda":"4.75","spell1Id":4,"spell2Id":7,"item0":3153,"item1":3094,"item2":3006,"item3":1055,"item4":1038,"item5":1037,"item6":3363,"champLevel":12,"creepscore":176,"creepscorepermin":"8.0","primaryPerkStyle":8000,"primaryPerk":8005,"subPerkStyle":8200},{"gameId":2972264757,"win":false,"summonerName":"C9 Sneaky","gameDuration":1502,"championId":29,"kills":5,"deaths":5,"assists":6,"kda":"2.20","spell1Id":4,"spell2Id":7,"item0":1054,"item1":3031,"item2":3006,"item3":3085,"item4":3086,"item5":0,"item6":3363,"champLevel":13,"creepscore":185,"creepscorepermin":"7.4","primaryPerkStyle":8000,"primaryPerk":8008,"subPerkStyle":8200},{"gameId":2972218014,"win":true,"summonerName":"C9 Sneaky","gameDuration":2226,"championId":145,"kills":11,"deaths":5,"assists":2,"kda":"2.60","spell1Id":4,"spell2Id":7,"item0":3031,"item1":3140,"item2":3085,"item3":3006,"item4":3124,"item5":3036,"item6":3363,"champLevel":17,"creepscore":318,"creepscorepermin":"8.6","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8200},{"gameId":2972221824,"win":false,"summonerName":"C9 Sneaky","gameDuration":1546,"championId":51,"kills":1,"deaths":7,"assists":3,"kda":"0.57","spell1Id":4,"spell2Id":7,"item0":3031,"item1":3087,"item2":3006,"item3":2031,"item4":1055,"item5":0,"item6":3363,"champLevel":12,"creepscore":176,"creepscorepermin":"6.8","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8200},{"gameId":2972182030,"win":false,"summonerName":"C9 Sneaky","gameDuration":1986,"championId":202,"kills":6,"deaths":7,"assists":7,"kda":"1.86","spell1Id":4,"spell2Id":7,"item0":3031,"item1":3508,"item2":3046,"item3":3047,"item4":3094,"item5":0,"item6":3363,"champLevel":15,"creepscore":262,"creepscorepermin":"7.9","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8200},{"gameId":2970513919,"win":true,"summonerName":"C9 Sneaky","gameDuration":1282,"championId":67,"kills":7,"deaths":3,"assists":7,"kda":"4.67","spell1Id":4,"spell2Id":7,"item0":3153,"item1":3124,"item2":1054,"item3":2055,"item4":3006,"item5":1033,"item6":3363,"champLevel":12,"creepscore":144,"creepscorepermin":"6.7","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8100},{"gameId":2970428865,"win":true,"summonerName":"C9 Sneaky","gameDuration":1066,"championId":145,"kills":9,"deaths":1,"assists":5,"kda":"14.00","spell1Id":4,"spell2Id":7,"item0":1055,"item1":3124,"item2":3095,"item3":3006,"item4":1042,"item5":1051,"item6":3363,"champLevel":11,"creepscore":160,"creepscorepermin":"9.0","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8100},{"gameId":2970424455,"win":true,"summonerName":"C9 Sneaky","gameDuration":926,"championId":67,"kills":5,"deaths":2,"assists":4,"kda":"4.50","spell1Id":4,"spell2Id":7,"item0":3153,"item1":2031,"item2":1054,"item3":3006,"item4":2055,"item5":1043,"item6":3340,"champLevel":9,"creepscore":112,"creepscorepermin":"7.3","primaryPerkStyle":8000,"primaryPerk":8021,"subPerkStyle":8100},{"gameId":2970375170,"win":true,"summonerName":"C9 Sneaky","gameDuration":1844,"championId":15,"kills":10,"deaths":1,"assists":15,"kda":"25.00","spell1Id":4,"spell2Id":7,"item0":3095,"item1":2421,"item2":3094,"item3":3031,"item4":3072,"item5":3047,"item6":3363,"champLevel":16,"creepscore":275,"creepscorepermin":"8.9","primaryPerkStyle":8000,"primaryPerk":8008,"subPerkStyle":8200}]
      };

      // console.log(spells);
   }

   handleKeyPress = (event) => {
      if (event.key === 'Enter') {
         fetch('/summoner?name=' + this.state.summonerName)
            .then(response => response.json())
            .then(data => this.setState({ summonerMatchData: data }))
            .catch(error => console.log(error));
      }
   }

   handleChange = (event) => {
      this.setState({ summonerName: event.target.value });
   }

   findChampion = (id) => {
      for (let champion in champions.data) {
         if (champions.data[champion].key == id) {
            return champions.data[champion];
         } 
      }
      return null;
   }

   findSpell = (id) => {
      for (let spell in spells.data) {
         if (spells.data[spell].key == id) {
            return spells.data[spell];
         }
      }
      return null;
   }

   findPrimaryRune = (styleId, perkId) => {
      for (let i = 0; i < runes.length; i++) {
         let rune = runes[i];
         if (rune.id == styleId) {
            const keystones = rune.slots[0].runes;
            for (let j = 0; j < keystones.length; j++) {
               let keystone = keystones[j];
               if (keystone.id == perkId) {
                  return keystone;
               }
            }
         }
      }
      return null;
   }

   findSubRune = (styleId) => {
      for (let i = 0; i < runes.length; i++) {
         let rune = runes[i];
         if (rune.id == styleId) {
            return rune;
         }
      }
      return null;
   }

   findItem = (id) => {
      return items.data[id];
   }

   renderMatches = () => {
      let matchData = this.state.summonerMatchData;
      let matches = [];

      for (let i = 0; i < matchData.length; i++) {
         const match = matchData[i];
         const champion = this.findChampion(match.championId);
         const matchDuration = match.gameDuration;
         const timer = Math.floor(matchDuration / 60) + ":" + (matchDuration % 60);
         const spellList = [this.findSpell(match.spell1Id), this.findSpell(match.spell2Id)];
         const runeList = [this.findPrimaryRune(match.primaryPerkStyle, match.primaryPerk), this.findSubRune(match.subPerkStyle)];

         let itemList = [];
         for (let j = 0; j < 7; j++) {
            itemList.push(this.findItem(match['item' + j]));
         }
         itemList = itemList.filter(Boolean).map(item => <img src={'http://ddragon.leagueoflegends.com/cdn/6.24.1/img/item/' + item.image.full} />);

         matches.push(
            <div key={match.gameId} className="match">
               <div className='matchStats container'>
                  <p className='victory'>{match.win ? "Victory" : "Defeat"}</p>
                  <p className='gameTimer'>{timer}</p>
               </div>
               <div className="champion container">
                  <p>{champion.name}</p>
               </div>
               <div className='spells container'>
                  <div className='spells'>
                     <img src={'http://ddragon.leagueoflegends.com/cdn/6.24.1/img/spell/' + spellList[0].image.full} />
                     <img src={'http://ddragon.leagueoflegends.com/cdn/6.24.1/img/spell/' + spellList[1].image.full} />
                  </div>
                  <div className='runes'>
                     <img src={'../img/' + runeList[0].icon} />
                     <img src={'../img/' + runeList[1].icon} />
                  </div>
               </div>
               <div className='performance container'>
                  <p>{match.kills + '/' + match.deaths + '/' + match.assists}</p>
                  <p>{match.kda + ' KDA'}</p>
               </div>
               <div className='score container'>
                  <p>{match.creepscore + ' cs'}</p>
                  <p>{match.creepscorepermin + ' cs/m'}</p>
               </div>
               <div className='items container'>
                  {itemList}
               </div>
            </div>
         );
      }

      return (
         <div className='matchlist'>{matches}</div>
      );
   }

   render() {
      return (
         <div className="App">
            <div id="AppContainer">
               <input type='text' autoFocus='autofocus' onKeyPress={this.handleKeyPress} onChange={this.handleChange} />
               {this.renderMatches()}
            </div>
         </div>
      );
   }
}

export default App;
