import React, { Component } from 'react';
import runes from './lol_data/runesReforged.json';
import spells from './lol_data/summoner.json';
import items from './lol_data/item.json';
import champions from './lol_data/champion.json';
import './App.css';

class App extends Component {
   constructor() {
      super();

      this.state = {
         summonerName: "",
         summonerMatchData: []
      };

      // console.log(spells);
   }

   handleKeyPress = (event) => {
      if (event.key == 'Enter') {
         fetch('/summoner?name=' + this.state.summonerName)
            .then(response => response.json())
            .then(data => this.setState({ summonerMatchData: data }))
            .catch(error => console.log(error));
      }
   }

   handleChange = (event) => {
      this.setState({ summonerName: event.target.value });
   }

   renderMatches = () => {
      let matchData = this.state.summonerMatchData;
      let matches = [];

      console.log(champions);

      for (let i = 0; i < matchData.length; i++) {
         const match = matchData[i];
         const possibleChampions = champions.data.filter(champ => champ.key == match.championId);
         const champion = (possibleChampions.length > 0) ? possibleChampions[0] : null;

         matches.push(
            <div>
               <div className="champion">
                  {champion.name}
               </div>
            </div>
         );
      }

      return (
         <div className='matchlist'>{matches}</div>
      );
   }

   render() {
      return (
         <div className="App">
            <div id="AppContainer">
               <input type='text' autoFocus='autofocus' onKeyPress={this.handleKeyPress} onChange={this.handleChange} />
               {this.renderMatches()}
            </div>
         </div>
      );
   }
}

export default App;
